#
# Structure for table "article"
#

DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(1024) DEFAULT NULL,
  `forward` varchar(10240) DEFAULT NULL,
  `like_count` varchar(128) DEFAULT NULL,
  `img_url` varchar(1024) DEFAULT NULL,
  `post_date` varchar(1024) DEFAULT NULL,
  `category` varchar(128) DEFAULT NULL,
  `content_id` varchar(128) DEFAULT NULL,
  `author_name` varchar(1024) DEFAULT NULL,
  `hp_title` varchar(1024) DEFAULT NULL,
  `hp_author` varchar(1024) DEFAULT NULL,
  `hp_content` text,
  `is_hot` int(11) DEFAULT NULL,
  `is_main_show` int(11) DEFAULT NULL,
  `location` varchar(128) DEFAULT NULL,
  `is_delete` int(11) DEFAULT NULL,
  `is_recommend` int(11) DEFAULT NULL,
  `type1` varchar(128) DEFAULT '',
  `type2` varchar(128) DEFAULT '',
  `create_time` datetime DEFAULT NULL,
  `type` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `location` (`title`(255))
) ENGINE=InnoDB AUTO_INCREMENT=12785 DEFAULT CHARSET=utf8;

