INSERT INTO `user` VALUES (2,'admin','cdf4a007e2b02a0c49fc9b7ccfbb8a10c644f635e1765dcf2a7ab794ddc7edac','admin','upload/head-image-2-1545810993.png','3333',NULL,NULL,'13611112222',NULL,NULL,NULL,'2017-12-17 00:00:00','23283529a20ce4f00a250428463a27a0'),(3,'zhangsan','cdf4a007e2b02a0c49fc9b7ccfbb8a10c644f635e1765dcf2a7ab794ddc7edac',NULL,'images/head.png',NULL,NULL,NULL,'phone',NULL,NULL,NULL,NULL,'2a94f3f7bbe439a4eba9ff63090b1b0b'),(4,'lisi','cdf4a007e2b02a0c49fc9b7ccfbb8a10c644f635e1765dcf2a7ab794ddc7edac',NULL,'images/head.png','hehe',NULL,NULL,'136',NULL,NULL,NULL,NULL,NULL),(5,'cccc','8628088e2ec45ee84c3acdde127543b8576a8de49b23a867a6e13a9762109a4c',NULL,'images/head.png',NULL,NULL,NULL,'13222220000',NULL,NULL,NULL,NULL,NULL),(6,'aaaa','cd611a5e9326e4642b01d0077cbd32c9f34c4aa6fbb6c48ca14dbe2d0bd2591e',NULL,'images/head.png',NULL,NULL,NULL,'12322222222',NULL,NULL,NULL,NULL,NULL),(7,'chen','cd611a5e9326e4642b01d0077cbd32c9f34c4aa6fbb6c48ca14dbe2d0bd2591e',NULL,'upload/head-image-7-1551702757.png','rrrr',NULL,NULL,'17864309781',NULL,NULL,NULL,NULL,'f49f253f8518cf46d9e179bbb85d71f4'),(8,'aaaaa','8628088e2ec45ee84c3acdde127543b8576a8de49b23a867a6e13a9762109a4c',NULL,'images/head.png',NULL,NULL,NULL,'14522224444',NULL,NULL,NULL,NULL,'3ba9e8cd43b23eb107b3540806a04f28'),(9,'chao','cd611a5e9326e4642b01d0077cbd32c9f34c4aa6fbb6c48ca14dbe2d0bd2591e',NULL,'upload/head-image-9-1551423337.png',NULL,NULL,NULL,'15655552222',NULL,NULL,NULL,NULL,'0e8ee69bd74ba6f730a3462c6a7e050c'),(10,'test','cd611a5e9326e4642b01d0077cbd32c9f34c4aa6fbb6c48ca14dbe2d0bd2591e',NULL,'upload/head-image-10-1551423575.png',NULL,NULL,NULL,'17855552222',NULL,NULL,NULL,NULL,'662bb7195f204ab1e2bfae262c03fd60'),(11,'0000','cd611a5e9326e4642b01d0077cbd32c9f34c4aa6fbb6c48ca14dbe2d0bd2591e',NULL,'images/head.png',NULL,NULL,NULL,'15444444444',NULL,NULL,NULL,NULL,'b44a4bf538f77b9bb7e35268a9546e15');
